let urls=[],interval=5e3,currentIndex=0,timer;function startRotation(){var e=document.getElementById("urls").value,t=document.getElementById("interval").value;""===e?alert("Пожалуйста, введите хотя бы один URL"):(urls=e.split(",").map(e=>e.trim()),interval=1e3*parseInt(t),0<urls.length?loadNextPage():alert("Не найдено допустимых URL."))}function loadNextPage(){currentIndex>=urls.length&&(currentIndex=0),document.getElementById("webframe").src=urls[currentIndex],currentIndex++,timer=setTimeout(loadNextPage,interval)}function stopRotation(){clearTimeout(timer)}